import { Component } from '@angular/core';
import axios from 'axios';
import { StateService } from '../service/shared.service';
import Cookies from 'js-cookie';

@Component({
  selector: 'app-new-vacation',
  templateUrl: './new-vacation.component.html',
  styleUrls: ['./new-vacation.component.scss'],
})
export class NewVacationComponent {
  accountNo: number | undefined;
  action: string | undefined;
  amount: number | undefined;
  payments: number | undefined;
  rate: number | undefined;

  constructor(private stateService: StateService) {}

  submitForm(): void {
    const data: any = {
      accountNo: this.accountNo,
      action: this.action,
      amount: this.amount,
      payments: this.payments,
      rate: this.rate,
    };

   console.log(data)
    axios
      .post('http://localhost:8080/new', {
        data,
      })
      .then((response) => {
        // this.stateService.changeShowNewVacationModal(false);
        // this.stateService.getVacation();
        alert(response.data);
      })
      .catch(function (error) {
        alert(error);
        console.log(error);
      });
  }
  closeModal() {
    this.stateService.changeShowNewVacationModal(false);
  }
}
<div class="form-admin-container">
  <form class="admin-form" (ngSubmit)="submitForm()">
    <button class="close-admin-modal-icon" type="button" (click)="closeModal()">
      X
    </button>
    <h3 class="form-admin-header">What To Do ?</h3>
    <label class="form-admin-label">Account No :</label>
    <input
      type="number"
      name="accountNo"
      class="admin-form-input"
      [(ngModel)]="accountNo"
      #titleInput="ngModel"
      required
    />

    <label class="form-admin-label">Actions List :</label>
    <input
      list="list"
      type="list"
      name="action"
      class="admin-form-input"
      [(ngModel)]="action"
      #categoryInput="ngModel"
      required
    />
    <datalist id="list">
      <option value="Withdraw"></option>
      <option value="Deposit"></option>
      <option value="I Need A Loan"></option>
    </datalist>

    <label class="form-admin-label">Total Amount $ : </label>
    <input
      class="admin-form-textarea"
      type="number"
      name="amount"
      [(ngModel)]="amount"
      #descriptionInput="ngModel"
      required
    />
    <label class="form-admin-label">Loan Payments : </label>
    <input

      class="admin-form-input"
      type="number"
      name="payments"
      [(ngModel)]="payments"
      #destinationInput="ngModel"
      required
    />


    <label class="form-admin-label">Interest Rate (%) </label>
    <input
      class="admin-form-textarea"
      type="number"
      name="rate"
      [(ngModel)]="rate"
      #imgUrlInput="ngModel"
      required
    />
    <button class="submit-admin-form" type="submit">Submit</button>
  </form>
</div>
