import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { StateService } from '../service/shared.service';
import Cookies from 'js-cookie';
import axios from 'axios';

@Component({
  selector: 'app-vacations',
  templateUrl: './vacations.component.html',
  styleUrls: ['./vacations.component.css'],
})
export class VacationsComponent implements OnInit {
  vacations: any;
  showRegisterModal: any | undefined;
  showLoginModal: any | undefined;
  showNewVacationModal: any | undefined;
  nameOfUser: any | undefined;
  modeUser: any | undefined;
  modeAdmin: any | undefined;
  showVacationUpdate: boolean | undefined;
  selectedVacation: any;
  showVacationDelete: any;
  checkBox: number[] | undefined;

  constructor(private http: HttpClient, public stateService: StateService) {}

  ngOnInit() {
    // this.http.get('http://localhost:8080/vacations').subscribe((data) => {
    //   this.vacations = data;
    // });

    this.stateService.getVacation();
    this.stateService.currentVacations.subscribe((value) => {
      this.vacations = value;
    });
    this.stateService.currentModeUser.subscribe((value) => {
      this.modeUser = value;
    });
    this.stateService.currentModeAdmin.subscribe((value) => {
      this.modeAdmin = value;
    });

    this.stateService.currentShowRegisterModal.subscribe((value) => {
      this.showRegisterModal = value;
    });
    this.stateService.currentShowLoginModal.subscribe((value) => {
      this.showLoginModal = value;
    });
    this.stateService.currentShowNewVacationModal.subscribe((value) => {
      this.showNewVacationModal = value;
    });
    this.stateService.currentShowVacationUpdate.subscribe((value) => {
      this.showVacationUpdate = value;
    });
    this.stateService.currentShowVacationDelete.subscribe((value) => {
      this.showVacationDelete = value;
    });
    this.stateService.currentNameOfUser.subscribe((value) => {
      this.nameOfUser = value;
    });
    this.stateService.currentCheckBox.subscribe((value) => {
      this.checkBox = value;
    });

    this.stateService.checkLogin();
  }

  loggin() {
    this.stateService.changeShowLoginModal(true);
    this.stateService.changeModeUser(false);
    this.stateService.changeModeAdmin(false);
    Cookies.remove('token');
  }

  logout() {
    this.stateService.changeShowLoginModal(true);
    this.stateService.changeModeUser(false);
    this.stateService.changeModeAdmin(false);
    Cookies.remove('token');
  }

  addNewVacation() {
    this.stateService.changeShowLoginModal(false);
    this.stateService.changeModeUser(false);
    this.stateService.changeShowNewVacationModal(true);
  }

  handleUpdateEvent(vacation: any) {
    this.selectedVacation = vacation;
    this.stateService.changeShowVacationUpdate(true);
  }
  handleDeleteEvent(vacation: any) {
    this.selectedVacation = vacation;
    this.stateService.changeShowVacationDelete(true);
  }
  handleCheck(vacation: any) {
    this.selectedVacation = vacation;
    const jwt = Cookies.get('token');
    axios
      .patch(`http://localhost:8080/fav/${vacation._id}`, {
        withCredentials: true,
        headers: { authorization: `Bearer ${jwt}` },
      })
      .then((response) => {
        const data = response.data;
        if (data.length && Array.isArray(data)) {
          const checkBoxIds = data
            .map((item: { vacation_id: any }) => item.vacation_id)
          this.stateService.changeCheckBox(checkBoxIds);
        }
        this.stateService.checkFav();
      })
      .catch(function (error) {
        alert(error);
      });
  }
}
<div *ngIf="vacations">
  <app-login></app-login>
  <app-register></app-register>
  <app-new-vacation></app-new-vacation>
  <app-update-vacation [vacation]="selectedVacation"></app-update-vacation>
  <app-delete-vacation
    [vacation]="selectedVacation"
  ></app-delete-vacation>
  <div id="header">
    <ng-container *ngIf="modeUser; else guest">
      <div class="user-guest-div">
        <p class="hello-login-loguot">Hello, {{ nameOfUser }}...</p>
        <button type="button" class="logout-btn" (click)="logout()">
          Logout
        </button>
      </div>
    </ng-container>
    <ng-template #guest>
      <ng-container
        *ngIf="!modeUser && !modeAdmin && !showLoginModal; else admin"
      >
        <div class="user-guest-div">
          <p class="hello-login-loguot">Hello, {{ nameOfUser }}...</p>
          <button type="button" class="loggin-btn" (click)="loggin()">
            Loggin
          </button>
        </div>
      </ng-container>
    </ng-template>
    <ng-template #admin>
      <ng-container *ngIf="modeAdmin">
        <div class="user-guest-div">
          <p class="hello-login-loguot">Hello, {{ nameOfUser }}... (Admin)</p>
          <button type="button" class="logout-btn" (click)="logout()">
            Logout
          </button>
          <button type="button" class="logout-btn" (click)="addNewVacation()">
            Add New Vacation
          </button>
        </div>
      </ng-container>
    </ng-template>
    <div id="topHeader">monster Vaction</div>
  </div>

  <div
    id="container"
    *ngIf="
      !showVacationUpdate &&
      !showLoginModal &&
      !showRegisterModal &&
      !showNewVacationModal &&
      !showVacationDelete
    "
  >
    <div class="menu-item" *ngFor="let vacation of vacations">
      <img class="img-menu" src="{{ vacation.img_url }}" alt="" />
      <h1 class="menu-item-header">
        {{ vacation.title }} in {{ vacation.destination }}
      </h1>
      <p class="menu-date">
        From
        {{ vacation.date_start.slice(0, vacation.date_start.length - 14) }} To
        {{ vacation.date_end.slice(0, vacation.date_end.length - 14) }}
      </p>
      <p class="item-text">{{ vacation.description }}</p>
      <p class="price">Just $ {{ vacation.price }}</p>
      <button
        class="admin-delete-icon"
        *ngIf="modeAdmin && !modeUser"
        (click)="handleDeleteEvent(vacation)"
      >
        Del
      </button>
      <button
        class="admin-edit-icon"
        *ngIf="modeAdmin && !modeUser"
        (click)="handleUpdateEvent(vacation)"
      >
        Edit
      </button>
      <label for="like-follow-btn" class="like-follow-btn"></label>
      <!-- <input
        name="like-follow-btn"
        *ngIf="modeUser"
        type="radio"
        class="like-icon"
        (click)="handleCheck(vacation)"
        checked="checked"
      /> -->
      <img
        name="like-follow-btn"
        *ngIf="modeUser"
        class="like-icon"
        [src]="
          checkBox?.includes(vacation._id)
            ? '../../assets/lib/icon-favorite/Active.png'
            : '../../assets/lib/icon-favorite/Inactive.png'
        "
        (click)="handleCheck(vacation)"
      />
    </div>
  </div>
</div>
