import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { StateService } from '../service/shared.service';
import Cookies from 'js-cookie';
import axios from 'axios';

@Component({
  selector: 'app-vacations',
  templateUrl: './vacations.component.html',
  styleUrls: ['./vacations.component.scss'],
})
export class VacationsComponent implements OnInit {
  actions: any;
  accountNo: number | undefined;

  constructor(private http: HttpClient, public stateService: StateService) {}

  ngOnInit() {


  submitAccounNumber() {
    const data  = {
      accountNo: this.accountNo,
    };

    axios
      .post('http://localhost:8080/find', {
        data,
      })
      .then((response) => {
        // this.stateService.changeActions(response.data);
        this.actions = response.data;
console.log(this.actions)
        // this.stateService.getVacation();
        // alert(response.data);
      })
      .catch(function (error) {
        alert(error);
        console.log(error);
      });
  }





}
  function submitAccounNumber() {
    throw new Error('Function not implemented.');
  }

<div >
  <app-new-vacation></app-new-vacation>
  <div class="form-admin-container">
    <form class="admin-form" (ngSubmit)="submitAccounNumber()">
      <button class="close-admin-modal-icon" type="button">
        X
      </button>
      <h3 class="form-admin-header">Account no </h3>
      <label class="form-admin-label">Account No :</label>
      <input
        type="number"
        name="accountNo"
        class="admin-form-input"
        [(ngModel)]="accountNo"
        #titleInput="ngModel"
        required
      />
      <button class="submit-admin-form" type="submit">Submit</button>
    </form>
  </div>
  <div id="container">
    <div class="menu-item" *ngFor="let action of actions">
      <h3>Account Number : {{ action.accountNo }} </h3>
      <h3>Action Type : {{ action.action }} </h3>
      <h3>Amount Of : {{ action.amount }} $</h3>
      <h3>Loan Payments :{{ action.payments }}</h3>
      <h3>Loan Rate : {{ action.rate }}%</h3>
    </div>
  </div>
</div>
