import { Component } from '@angular/core';
import axios from 'axios';
import { StateService } from '../service/shared.service';
import Cookies from 'js-cookie';

@Component({
  selector: 'app-new-vacation',
  templateUrl: './new-vacation.component.html',
  styleUrls: ['./new-vacation.component.scss'],
})
export class NewVacationComponent {
  title: string | undefined;
  category: string | undefined;
  description: string | undefined;
  destination: string | undefined;
  img_url: string | undefined;
  date_start: string | undefined;
  date_end: string | undefined;
  price: string | undefined;

  constructor(private stateService: StateService) {}

  submitForm(): void {
    const data: any = {
      title: this.title,
      category: this.category,
      description: this.description,
      destination: this.destination,
      img_url: this.img_url,
      date_start: this.date_start,
      date_end: this.date_end,
      price: this.price,
    };



    if (
      !data.title ||
      !data.description ||
      !data.destination ||
      !data.category ||
      !data.img_url ||
      !data.date_start ||
      !data.date_end ||
      !data.price
    ) {
      return alert('All rows must be filled');
    }
    const jwt = Cookies.get('token');
    axios
      .post('http://localhost:8080/new', {
        withCredentials: true,
        headers: { authorization: `Bearer ${jwt}` },
        data,
      })
      .then((response) => {
        this.stateService.changeShowNewVacationModal(false);
        this.stateService.getVacation();
        alert(response.data);
      })
      .catch(function (error) {
        alert(error);
        console.log(error);
      });
  }
  closeModal() {
    this.stateService.changeShowNewVacationModal(false);
  }
}
<div class="form-admin-container">
  <form class="admin-form" (ngSubmit)="submitForm()">
    <button class="close-admin-modal-icon" type="button" (click)="closeModal()">
      X
    </button>
    <h3 class="form-admin-header">What To Do ?</h3>
    <label class="form-admin-label">Account No :</label>
    <input
      type="text"
      name="title"
      class="admin-form-input"
      [(ngModel)]="title"
      #titleInput="ngModel"
      required
    />

    <label class="form-admin-label">Actions List :</label>
    <input
      list="list"
      type="text"
      name="category"
      class="admin-form-input"
      [(ngModel)]="category"
      #categoryInput="ngModel"
      required
    />
    <datalist id="list">
      <option value="Withdraw"></option>
      <option value="Deposit"></option>
      <option value="I Need A Loan"></option>
    </datalist>

    <label class="form-admin-label">Total Amount $ : </label>
    <input
      class="admin-form-textarea"
      type="text"
      name="description"
      [(ngModel)]="description"
      #descriptionInput="ngModel"
      required
    />
    <label class="form-admin-label">Loan Payments : </label>
    <input
      list="payments"
      class="admin-form-input"
      type="text"
      name="destination"
      [(ngModel)]="destination"
      #destinationInput="ngModel"
      required
    />
    <datalist id="payments">
      <option value="1"></option>
      <option value="2"></option>
      <option value="3"></option>
      <option value="4"></option>
      <option value="5"></option>
      <option value="6"></option>
      <option value="7"></option>
      <option value="8"></option>
      <option value="9"></option>
      <option value="10"></option>
    </datalist>

    <label class="form-admin-label">Interest Rate (%) </label>
    <input
      class="admin-form-textarea"
      type="text"
      name="img_url"
      [(ngModel)]="img_url"
      #imgUrlInput="ngModel"
      required
    />
    <!-- <div *ngIf="imgUrlInput.invalid && imgUrlInput.touched">
      <p>Img_url is required</p> -->
    <!-- </div> -->



    <button class="submit-admin-form" type="submit">Add Vacation</button>
    <!-- <div *ngIf="dateStartInput.invalid && dateStartInput.touched">
      <p>Date Start is required</p> -->
    <!-- </div> -->
  </form>
</div>
