import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { CookieService } from 'ngx-cookie-service';
import axios from 'axios';

@Injectable({
  providedIn: 'root',
})
export class StateService {
  actions = new BehaviorSubject<any>([]);
  currentActions = this.actions.asObservable();
  response: any;
  data: any;

  constructor(private http: HttpClient, private cookieService: CookieService) {}


  changeActions(value: any) {
    this.actions.next(value);
  }






  checkLogin() {
    const jwt = this.cookieService.get('token');
    if (jwt) {
      this.changeIsLogin(true);
      this.changeShowLoginModal(false);

      axios
        .get('http://localhost:8080/profile', {
          withCredentials: true,
          headers: { authorization: `Bearer ${jwt}` },
        })
        .then((response) => {
          const { userName, admin } = response.data;
          if (admin) {
            this.changeModeAdmin(true);
            this.changeModeUser(false);
            this.changeNameOfUser(userName);
          }
          if (!admin) {
            this.checkFav();
            this.changeModeUser(true);
            this.changeModeAdmin(false);
            this.changeNameOfUser(userName);
          }
        })
        .catch(function (error) {
          console.log(error.message);
          alert(error.message);
        });
    }
  }

  checkFav() {
    let checkBoxIds: any[] = [];

    const jwt = this.cookieService.get('token');
    axios
      .get('http://localhost:8080/fav', {
        withCredentials: true,
        headers: { authorization: `Bearer ${jwt}` },
      })
      .then((response) => {
        const data = response.data;
        if (data.length && Array.isArray(data)) {
          checkBoxIds = data.map(
            (item: { vacation_id: any }) => item.vacation_id
          );
        }
        this.changeCheckBox(checkBoxIds);
      })
      .catch(function (error) {
        alert(error);
      });
  }
  getVacation() {}

  getActions() {
    this.http.get('http://localhost:8080/actions').subscribe((data) => {
      this.changeActions(data);
    });
  }
}
